import { useMemo, useState } from 'react';
import { Layout, Menu } from 'antd';
import { BrowserRouter, Link } from 'react-router-dom';
import Home from './Home';
import Statistic from './Statistics';
import Contact from './Contact';
import Error404 from './Error404';
import './App.css';
const { Header, Content, Footer } = Layout;
const routes = [
  {
    path: '/home',
    component: Home,
  },
];
const App = () => {
  const [page, setPages] = useState<Number>(1);

  const returnCurrentPage = useMemo(() => {
    if (page === 1) {
      return <Home />;
    } else if (page === 2) {
      return <Statistic />;
    } else if (page === 3) {
      return <Contact />;
    } else {
      return <Error404 />;
    }
  }, [page]);
  const handlePageChange = (menuKey: string) => {
    return setPages(Number(menuKey));
  };
  return (
    <BrowserRouter>
      <Header>
        <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['1']}>
          <Menu.Item key="1" onClick={(menu) => handlePageChange(menu.key)}>
            <Link to="/home">Home</Link>
          </Menu.Item>
          <Menu.Item key="2" onClick={(menu) => handlePageChange(menu.key)}>
            <Link to="/statistic">Statistic</Link>
          </Menu.Item>
          <Menu.Item key="3" onClick={(menu) => handlePageChange(menu.key)}>
            <Link to="/contact">Contact</Link>
          </Menu.Item>
        </Menu>
      </Header>
      <Content
        className="site-layout"
        style={{ padding: '0 50px', marginTop: 64 }}
      >
        <div
          className="site-layout-background"
          style={{ padding: 24, minHeight: 380 }}
        >
          <>{returnCurrentPage}</>
        </div>
      </Content>
      <Footer style={{ textAlign: 'center' }}>
        Ant Design Â©2018 Created by Ant UED
      </Footer>
    </BrowserRouter>
  );
};
export default App;
